export default class Logger {
    /**
     * @param {object} props
     * @param {string} [props.name]
     * @param {string} [props.level]
     */
    constructor(props?: {
        name?: string | undefined;
        level?: string | undefined;
    });
    /**
     * @private
     * @type {?string}
     */
    private _name;
    /**
     * @private
     * @type {?string}
     */
    private _level;
    /**
     * Set the level of the Logger.
     *
     * @param {string} name
     * @returns {void}
     */
    setLoggerName(name: string): void;
    /**
     * @returns {?string}
     */
    get name(): string | null;
    /**
     * Set the level of the Logger.
     *
     * @param {string} level
     * @returns {void}
     */
    setLoggerLevel(level: string): void;
    /**
     * @returns {?string}
     */
    get level(): string | null;
}
